library(conflicted)
library(readr)
library(MASS)
library(dplyr)
library(ggplot2)
library(janitor)
library(purrr)

conflicts_prefer(dplyr::select)
conflicts_prefer(dplyr::filter)


eqtlgen &lt;- read_tsv(here::here("data", "eqtlgen.tsv"),
                    show_col_types = FALSE, 
                    name_repair = make_clean_names) |&gt; 
  rename(p_value = pvalue) |&gt; 
  mutate(log10_dist = log(dist, base = 10)) |&gt;
  filter(log10_dist &gt; 0) 


weibull_param &lt;- eqtlgen |&gt; 
  pull(log10_dist) |&gt; 
  fitdistr("weibull", lower = c(0, 0)) |&gt; 
  pluck("estimate") |&gt; 
  as.list()

normal_param &lt;- eqtlgen$log10_dist |&gt; 
  fitdistr("normal") |&gt; 
  pluck("estimate") |&gt; 
  as.list()

gamma_param &lt;- eqtlgen |&gt; 
  pull(log10_dist) |&gt; 
  fitdistr("gamma", lower = c(0, 0)) |&gt; 
  pluck("estimate") |&gt; 
  as.list()

eqtlgen |&gt; 
  ggplot(aes(log10_dist)) + 
  geom_histogram(aes(y = after_stat(density)), 
                 binwidth = 0.25, colour = "black", fill = "lightgrey") + 
  geom_function(aes(col = "Weibull"), fun = dweibull, args = weibull_param) +
  geom_function(aes(col = "Normal"), fun = dnorm, args = normal_param) +
  geom_function(aes(col = "Gamma"), fun = dgamma, args = gamma_param) +
  scale_colour_manual(values = c("red","black","purple")) + 
  labs(
    colour = "Distribution",
    y = "Density", 
    x = "Log(10) distance from variant to TSS of cognate gene"
  ) + 
  theme_bw()
